class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      addy: undefined,
      eth: window.ethereum,
      modalOpen: false,
      isMintPage: true
    };
  }

  async connectWallet() {
    let LOCK = false;
    const { eth } = this.state;

    if (!eth) {
      this.openModal(
        "No wallet found",
        "No wallets are detected, please install MetaMask"
      );
      return;
    }

    if (LOCK) {
      return;
    }

    LOCK = true;

    try {
      await eth.request({
        method: "wallet_switchEthereumChain",
        params: [{ chainId: "0x61" }]
      });

      const temp = await eth.request({ method: "eth_requestAccounts" });
      this.setState({ addy: temp[0] });
    } catch (err) {
      this.openModal("Wallet connection error", "Unable to connect wallet!");
      return;
    } finally {
      LOCK = false;
    }
    this.openModal(
      "Wallet connected!",
      "Congratulations! Your wallet has been successfully connected to the dapp. You can now start using the features and functionalities available to you. Enjoy!"
    );
  }

  openModal(header, body) {
    this.setState({ modalOpen: true });
    simpleModal.show(header, body);
  }

  componentDidMount() {
    const { eth } = this.state;
    if (eth) {
      eth.on("accountsChanged", (accounts) => {
        this.setState({ addy: accounts[0] });
      });
      const addy2 = eth.selectedAddress;
      if (addy2) {
        this.setState({ addy: addy2 });
      }
    }
  }

  componentWillUnmount() {
    const { eth } = this.state;
    if (eth) {
      eth.removeAllListeners("accountsChanged");
    }
  }

  render() {
    const { addy, modalOpen, isMintPage } = this.state;
    const setState2 = this.setState.bind(this);
    return (
      <div style={{ minHeight: "100vh" }}>
        <ModalExample style={{ zIndex: 3 }} isOpen={modalOpen} />

        <div className="container2">
          <div>
            <br />
          </div>
          <Row>
            <img
              src="gashaponlogo-1.png"
              alt="Gashapon logo"
              className="col s12 l4"
            />
            {addy ? (
              <p
                className="col s6 l3 right"
                style={{
                  display: "block",
                  color: "black",
                  textOverflow: "ellipsis",
                  overflow: "hidden"
                }}
              >
                {addy}
              </p>
            ) : (
              <Button
                onClick={this.connectWallet.bind(this)}
                waves="light"
                elem="button"
                className="col s6 l3 right"
              >
                Connect Wallet
              </Button>
            )}
            {addy && (
              <Button
                waves="light"
                elem="button"
                className="col s6 l3 right"
                styles={{ display: "inline-block" }}
                onClick={function () {
                  setState2({ isMintPage: !isMintPage });
                }}
              >
                {isMintPage ? "dashboard" : "mint"}
              </Button>
            )}
          </Row>
          {isMintPage ? (
            <MintComponent
              notconnected={!addy}
              openModal={this.openModal.bind(this)}
            ></MintComponent>
          ) : addy ? (
            <NFTList addy={addy}></NFTList>
          ) : (
            <div className="App blue-grey darken-3"></div>
          )}
        </div>
      </div>
    );
  }
}

export default App;
