import React, { Component } from "react";
import { Button, Row, Col, Collapsible } from "react-materialize";
import Web3 from "web3";
import "./styles.css";
import { web3_eth, NFTContract, PaymentTokenContract } from "./Common";

const toBigNumber = Web3.utils.toBN;
const minimumApprove = toBigNumber("340282366920938463463374607431768211456");

const eth = window.ethereum;

let ERC20Methods = new web3_eth.Contract(
  [
    {
      inputs: [
        { internalType: "address", name: "owner", type: "address" },
        { internalType: "address", name: "spender", type: "address" }
      ],
      name: "allowance",
      outputs: [{ internalType: "uint256", name: "", type: "uint256" }],
      stateMutability: "view",
      type: "function"
    }
  ],
  PaymentTokenContract
).methods;
const ERC20Approve =
  "0x095ea7b3000000000000000000000000" +
  NFTContract.slice(2).toLowerCase() +
  "ffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff";

const ERC20Allowance = ERC20Methods.allowance;
ERC20Methods = undefined;
let contractMethods = new web3_eth.Contract(
  [
    {
      inputs: [
        { internalType: "address", name: "receiver", type: "address" },
        { internalType: "uint256", name: "quantity", type: "uint256" },
        { internalType: "bytes", name: "data", type: "bytes" }
      ],
      name: "mint",
      outputs: [],
      stateMutability: "nonpayable",
      type: "function"
    },
    {
      inputs: [],
      name: "count",
      outputs: [{ internalType: "uint256", name: "", type: "uint256" }],
      stateMutability: "view",
      type: "function"
    }
  ],
  NFTContract
).methods;
const totalSupply = contractMethods.count().call;
const mint = contractMethods.mint;
const sendTransaction = eth
  ? function (to, data) {
      return eth.request({
        method: "eth_sendTransaction",
        params: [
          {
            from: eth.selectedAddress,
            to: to,
            data: data,
            chainId: "0x61"
          }
        ]
      });
    }
  : undefined;
contractMethods = undefined;

class MintComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      quantity: 1,
      mintCount: 0
    };
    this.openModal = props.openModal;
    this.notconnected = props.notconnected;
  }

  handleDecrementClick = () => {
    const { quantity } = this.state;
    if (quantity > 1) {
      this.setState({ quantity: quantity - 1 });
    }
  };

  handleIncrementClick = () => {
    const { quantity } = this.state;
    this.setState({ quantity: quantity + 1 });
  };

  handleX10Click = () => {
    this.setState({ quantity: 10 });
  };

  handleX50Click = () => {
    this.setState({ quantity: 50 });
  };

  handleX100Click = () => {
    this.setState({ quantity: 100 });
  };

  render() {
    const { quantity } = this.state;

    return (
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          flexWrap: "nowrap"
        }}
      >
        <Row id="offsetbtn">
          <div className="col s12">
            <img
              src=""
              alt=""
              style={{ maxWidth: "3em", float: "right" }}
            />
          </div>
          <div className="col s12">
            <br />
          </div>
          <div className="col s12">
            <img
              src=""
              alt=""
              style={{ maxWidth: "3em", float: "right" }}
            />
          </div>
          <div className="col s12">
            <br />
          </div>
          <div className="col s12">
            <img
              src="twitter.png"
              alt=""
              style={{ maxWidth: "3em", float: "right" }}
            />
          </div>
        </Row>
        <Row
          id="rules"
          style={{
            position: "absolute",
            zIndex: 10,
            left: 0,
            width: "100%"
          }}
        >
          <Row className="col s9 m6 l4">
            <Collapsible
              className="col s12"
              style={{ backgroundColor: "#ffce19" }}
            >
              <li
                className="col s12"
                style={{
                  backgroundColor: "#ffce19"
                }}
              >
                <img
                  className="collapsible-body"
                  style={{
                    backgroundColor: "#ffce19",
                    width: "100%"
                  }}
                  src="gashapon-rules.png"
                  alt="rules"
                />
                <div
                  className="collapsible-header"
                  style={{
                    backgroundColor: "#ffce19"
                  }}
                >
                  <strong className="flow-text">rules</strong>
                </div>
              </li>
            </Collapsible>
          </Row>
        </Row>

        <Row
          style={{ position: "absolute", zIndex: 8, bottom: "25%" }}
          id="machinescontainer"
        >
          <img
            className="col s6 offset-s3"
            src="gashaponautomaaten.png"
            alt="Gashapon machines"
            id="largebg"
          />
          <img
            className="col s10 offset-s1 m6 offset-m3"
            src="mobilemachines.png"
            alt="Gashapon machines"
            id="smallbg"
          />
        </Row>
        <div
          style={{
            width: "100vw",
            backgroundImage: "linear-gradient(#616060, black)",
            position: "absolute",
            bottom: "0px",
            marginTop: "-5em"
          }}
        >
          <div className="container2 row">
            <div className="col s12 l6 offset-s0 offset-l3">
              <Row>
                <div>
                  <br />
                  <br />
                </div>
                <div
                  className="col s2"
                  style={{
                    fontSize: "3em",
                    userSelect: "none",
                    color: "#ff7bac",
                    textAlign: "right"
                  }}
                  onClick={this.handleDecrementClick}
                >
                  -
                </div>
                <input
                  style={{
                    display: "block",
                    alignItems: "center",
                    justifyContent: "center",
                    textAlign: "center",
                    color: "white"
                  }}
                  type="text"
                  value={quantity}
                  readOnly
                  className="col s8"
                />
                <div
                  className="col s2"
                  onClick={this.handleIncrementClick}
                  style={{
                    fontSize: "3em",
                    userSelect: "none",
                    color: "#ff7bac"
                  }}
                >
                  +
                </div>
              </Row>
              <div>
                <br />
              </div>
              <Row>
                <center className="col s4">
                  <Button
                    className="darkpinkborder"
                    small
                    onClick={this.handleX10Click}
                    style={{ width: "90%", display: "block" }}
                  >
                    X10
                  </Button>
                </center>
                <center className="col s4">
                  <Button
                    className="darkpinkborder"
                    small
                    style={{ width: "90%", left: "50%", display: "block" }}
                    onClick={async function () {
                      const addy = eth.selectedAddress;
                      try {
                        if (
                          minimumApprove.gt(
                            toBigNumber(
                              await ERC20Allowance(addy, NFTContract).call()
                            )
                          )
                        ) {
                          await sendTransaction(
                            PaymentTokenContract,
                            ERC20Approve
                          );
                        }
                        await sendTransaction(
                          NFTContract,
                          mint(
                            addy,
                            this.state.quantity.toString(),
                            "0x"
                          ).encodeABI()
                        );
                      } catch (e) {
                        this.openModal(
                          "Unable to mint NFT",
                          e?.message ??
                            "An unknown error occoured while minting NFT"
                        );
                        return;
                      }
                      this.openModal(
                        "NFT minted successfully",
                        "Congratulations! Your NFT has been successfully minted and added to your collection."
                      );
                    }.bind(this)}
                    disabled={this.notconnected && !eth?.selectedAddress}
                  >
                    Mint
                  </Button>
                </center>
                <Col s={4}>
                  <Button
                    className="darkpinkborder"
                    small
                    onClick={this.handleX100Click}
                    style={{ width: "90%", left: "50%", display: "block" }}
                  >
                    X100
                  </Button>
                </Col>
              </Row>
            </div>
          </div>
        </div>
      </div>
    );
  }
  async updateTotalSupply() {
    if (this.totalSupplyLock) {
      return;
    }
    this.totalSupplyLock = true;
    try {
      this.setState({ mintCount: parseInt(await totalSupply()) });
    } finally {
      this.totalSupplyLock = false;
    }
  }
}

export default MintComponent;
